import React from 'react';

const Calendar = () => {
    var curr = new Date; // get current date
    var first = curr.getDate() - curr.getDay(); // First day is the day of the month - the day of the week
    var last = first + 6; // last day is the first day + 6
    
    var firstday = new Date(curr.setDate(first)).getDate();
    var lastday = new Date(curr.setDate(last)).toUTCString();
    var week_array = Array.from({length: 7}, (_, i) => i + firstday)
    const day_name = ["א","ב","ג","ד","ה","ו","ש"]
    var object = {};
    week_array.forEach(function (k, i) {
        object[k] = value[i];
    })
    return (
        <>
        <div className='text-center h-1/4 pt-[20px]'>
            15/01-21/01
        </div>
        <div className='grid grid-cols-7 gap-2 h-2/4 text-center pt-[20px] mx-[10px] text-white '>
            {
                week_array.map( ([day,date]) => {
                    return(
                        <div className='day rounded-[100px]'>
                        <div className='h-[70px]  '>
                            <div className='  w-[30px] h-[30px] rounded-full  m-auto mt-[5px]'>
                                {date}
                            </div>
                            
                        </div>
                        <div>
                            {day}  
                        </div>
                    </div>
                    );
                })
            }
            <div className='day bg-black rounded-[100px]'>
                <div className='h-[70px]  '>
                    <div className='  w-[30px] h-[30px] bg-white rounded-full text-black m-auto mt-[5px]'>
                        {firstday}
                    </div>
                    
                </div>
                <div>
                    S
                </div>
            </div>
            
        </div>
        <div className='text-right pt-[20px] h-1/4 bg-lavander'>
            some characters
        </div>
        </>
    );
}

export default Calendar;
