import React, { useEffect, useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

import "./styles.css";
import "./index.css"
// import required modules
import { Pagination } from "swiper";

export default function App() {
  const windowSize = useRef([window.innerWidth, window.innerHeight]);

  const [mobileView, setMobileView] = useState(false);
  useEffect(() => {
    if (window.innerWidth > 767) {
      setMobileView(false);
    } else if (window.innerWidth < 767) {
      setMobileView(true);
    }
  }, []);
  return (

    <><div className="h-[250px] bg-black">

    </div>
    <div className="h-[50px] bg-lavander ">
    <Swiper

        slidesPerView={4.5}
        spaceBetween={5}

        className="mySwiper"

      >

        <SwiperSlide>
          <div className="w-[30px]"></div>
          <div className="w-[75px] h-[30px] bg-white rounded-[15px]">
            24:00
          </div>
        </SwiperSlide>
        <SwiperSlide className="">
          <div className="w-[67px] h-[30px]  rounded-[15px]">
            23:00
          </div>
        </SwiperSlide>
        <SwiperSlide className="">
          <div className="w-[67px] h-[30px]  rounded-[15px]">
            22:00
          </div>
        </SwiperSlide>
        <SwiperSlide className="">
          <div className="w-[67px] h-[30px]  rounded-[15px] ">
            21:00
          </div>
        </SwiperSlide>
        <SwiperSlide className="">
          <div className="w-[67px] h-[30px] rounded-[15px]">
            20:00
          </div>
        </SwiperSlide>
        <SwiperSlide className="">
          <div className="w-[67px] h-[30px] rounded-[15px] ">
            19:00
          </div>
          <div className="w-[30px]"></div>
        </SwiperSlide>



      </Swiper>
    </div>
    <div  className="bg-lavander h-auto">
      <Swiper
        pagination={{
          clickable: true,
        }}
        modules={[Pagination]}
        slidesPerView={1.25}
        spaceBetween={24}
        centeredSlides={true}
        className="mySwiper"

      >
      <></>
        <SwiperSlide className="bg-lavander">
          <div className="w-[304.06px] h-[349px] bg-white rounded-[30px] mt-[-175px]">
          <h2>Width: {windowSize.current[0]}</h2>
          <p> Mobile View: {mobileView}</p>
          <h2>Height: {windowSize.current[1]}</h2>
          </div>
        </SwiperSlide>

        <SwiperSlide className="bg-lavander">
        <div className="w-[304.06px] h-[349px] bg-white rounded-[30px] mt-[-175px]">
            slide 2
          </div>
        </SwiperSlide >
        <SwiperSlide className="bg-lavander" >
        <div className="w-[304.06px] h-[349px] bg-white rounded-[30px] mt-[-175px]">
            slide 3
          </div>
        </SwiperSlide>
        <SwiperSlide className="bg-lavander" >
        <div className="w-[304.06px] h-[349px] bg-white rounded-[30px] mt-[-175px]">
            slide 4
          </div>
        </SwiperSlide>

      </Swiper>
      </div>
    </>
  );
}
