import React, { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

import "./styles.css";
import "./index.css"
// import required modules
import { Pagination } from "swiper";

const swiper = new Swiper('.swiper', {
  // Default parameters
  slidesPerView: 1,
  spaceBetween: 10,
  // Responsive breakpoints
  breakpoints: {
    // when window width is >= 320px
    320: {
      slidesPerView: 2,
      spaceBetween: 20
    },
    // when window width is >= 480px
    480: {
      slidesPerView: 3,
      spaceBetween: 30
    },
    // when window width is >= 640px
    640: {
      slidesPerView: 4,
      spaceBetween: 40
    }
  }
})
export default function App() {
  return (
    <>
      <swiper
        slidesPerView={2}
        spaceBetween={24}
        centeredSlides={true}

        className="mySwiper bg-black h-[600px] "
      >
      <></>
        <SwiperSlide >
          <div className="w-[304.06px] h-[349px] bg-lavander">
            slide 1
          </div>
        </SwiperSlide>

        <SwiperSlide >
        <div className="w-[304.06px] h-[349px] bg-lavander">
            slide 2
          </div>
        </SwiperSlide>
        <SwiperSlide >
        <div className="w-[304.06px] h-[349px] bg-lavander">
            slide 3
          </div>
        </SwiperSlide>
        <SwiperSlide >
        <div className="w-[304.06px] h-[349px] bg-lavander">
            slide 4
          </div>
        </SwiperSlide>

      </swiper>
    </>
  );
}
